import React, { useState, useEffect } from "react";
import axios from 'axios';

function Article() {
  const [article, setArticle] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [newArticle, setNewArticle] = useState({ title: '', body: '' });

  useEffect(() => {
    // Fetch existing articles on component mount
    axios.get("http://localhost:4000/articles", {
      headers: { Accept: "application/json" }
    })
    .then(response => {
      setArticle(response.data);
      setLoading(false);
    })
    .catch(error => {
      setError(error);
      setLoading(false);
    });
  }, []);

  const createArticle = () => {
    // Ensure title and body are not empty before creating article
    if (!newArticle.title || !newArticle.body) {
      setError({ message: 'Title and Body are required' });
      return;
    }

    axios.post("http://localhost:4000/articles", newArticle, {
      headers: { Accept: "application/json", "Content-Type": "application/json" }
    })
    .then(response => {
      setArticle([...article, response.data]);
      setNewArticle({ title: '', body: '' });
      setError(null); // Clear any previous error
    })
    .catch(error => {
      setError(error);
    });
  };

  const updateArticle = (id, updatedArticle) => {
    axios.put(`http://localhost:4000/articles/${id}`, updatedArticle, {
      headers: { Accept: "application/json", "Content-Type": "application/json" }
    })
    .then(response => {
      setArticle(article.map(article => (article.id === id ? response.data : article)));
    })
    .catch(error => {
      setError(error);
    });
  };

  const deleteArticle = (id) => {
    axios.delete(`http://localhost:4000/articles/${id}`, {
      headers: { Accept: "application/json" }
    })
    .then(() => {
      setArticle(article.filter(article => article.id !== id));
    })
    .catch(error => {
      setError(error);
    });
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div className="card-container">
      <div className="card">
        <input
          type="text"
          placeholder="Title"
          value={newArticle.title}
          onChange={(e) => setNewArticle({ ...newArticle, title: e.target.value })}
        />
        <input
          type="text"
          placeholder="Body"
          value={newArticle.body}
          onChange={(e) => setNewArticle({ ...newArticle, body: e.target.value })}
        />
        <button onClick={createArticle}>Create Article</button>
        {error && <div>Error: {error.message}</div>}
      </div>
      {article.map(item => (
        <div className="card" key={item.id}>
          <h2>{item.title}</h2>
          <p>{item.body}</p>
          <button onClick={() => updateArticle(item.id, { title: 'Updated Title', body: 'Updated Body' })}>Update Article</button>
          <button onClick={() => deleteArticle(item.id)}>Delete Article</button>
        </div>
      ))}
    </div>
  );
}

export default Article;
