import React, { useState, useEffect } from "react";
import axios from 'axios';

function Article() {
  const [articles, setArticles] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [newArticle, setNewArticle] = useState({ title: '', body: '', published: false });

  useEffect(() => {
    // Fetch existing articles on component mount
    axios.get("http://localhost:4000/articles", {
      headers: { Accept: "application/json" }
    })
    .then(response => {
      setArticles(response.data);
      setLoading(false);
    })
    .catch(error => {
      setError(error);
      setLoading(false);
    });
  }, []);

  const createArticle = () => {
    // Ensure title and body are not empty before creating article
    if (!newArticle.title || !newArticle.body) {
      setError({ message: 'Title and Body are required' });
      return;
    }

    axios.post("http://localhost:4000/articles", newArticle, {
      headers: { Accept: "application/json", "Content-Type": "application/json" }
    })
    .then(response => {
      setArticles([...articles, response.data]);
      setNewArticle({ title: '', body: '', published: false });
      setError(null); // Clear any previous error
    })
    .catch(error => {
      setError(error);
    });
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewArticle(prevArticle => ({
      ...prevArticle,
      [name]: name === 'published' ? e.target.checked : value
    }));
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div className="card-container">
      <div className="card">
        <input
          type="text"
          placeholder="Title"
          value={newArticle.title}
          onChange={(e) => handleInputChange(e)}
          name="title"
        />
        <input
          type="text"
          placeholder="Body"
          value={newArticle.body}
          onChange={(e) => handleInputChange(e)}
          name="body"
        />
        <label>
          Published:
          <input
            type="checkbox"
            checked={newArticle.published}
            onChange={(e) => handleInputChange(e)}
            name="published"
          />
        </label>
        <button onClick={createArticle}>Create Article</button>
        {error && <div>Error: {error.message}</div>}
      </div>
      {articles.map(item => (
        <div className="card" key={item.id}>
          <h2>{item.title}</h2>
          <p>{item.body}</p>
          <p>Published: {item.published ? 'Yes' : 'No'}</p>
        </div>
      ))}
    </div>
  );
}

export default Article;
