import React, { useState, useEffect } from "react";
import axios from 'axios';

function Article() {
  const [articles, setArticles] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [newArticle, setNewArticle] = useState({ title: '', body: '', published: false });

  useEffect(() => {
    fetchArticles();
  }, []);

  const fetchArticles = () => {
    axios.get("http://localhost:4000/articles", {
      headers: { Accept: "application/json" }
    })
    .then(response => {
      setArticles(response.data);
      setLoading(false);
    })
    .catch(error => {
      setError(error);
      setLoading(false);
    });
  };

  const createArticle = () => {
    if (!newArticle.title || !newArticle.body) {
      setError({ message: 'Title and Body are required' });
      return;
    }

    axios.post("http://localhost:4000/articles", newArticle, {
      headers: { Accept: "application/json", "Content-Type": "application/json" }
    })
    .then(response => {
      setArticles([...articles, response.data]);
      setNewArticle({ title: '', body: '', published: false });
      setError(null);
    })
    .catch(error => {
      setError(error);
    });
  };

  const updateArticle = (id, updatedFields) => {
    axios.put(`http://localhost:4000/articles/${id}`, updatedFields, {
      headers: { Accept: "application/json", "Content-Type": "application/json" }
    })
    .then(response => {
      const updatedArticles = articles.map(article =>
        article.id === id ? { ...article, ...response.data } : article
      );
      setArticles(updatedArticles);
      setError(null);
    })
    .catch(error => {
      setError(error);
    });
  };

  const deleteArticle = async (id) => {
    try {
      await axios.delete(`http://localhost:4000/articles/${id}`);
      setArticles(articles.filter(article => article.id !== id)); // Remove deleted article from state
      console.log('Article deleted successfully');
    } catch (error) {
      console.error('Error deleting article:', error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    const newValue = type === 'checkbox' ? checked : value;
    setNewArticle(prevArticle => ({
      ...prevArticle,
      [name]: newValue
    }));
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div className="card-container">
      <div className="card">
        <input
          type="text"
          placeholder="Title"
          value={newArticle.title}
          onChange={handleInputChange}
          name="title"
        />
        <textarea
          placeholder="Body"
          value={newArticle.body}
          onChange={handleInputChange}
          name="body"
        />
        <label>
          Published:
          <input
            type="checkbox"
            checked={newArticle.published}
            onChange={handleInputChange}
            name="published"
          />
        </label>
        <button onClick={createArticle}>Create Article</button>
        {error && <div>Error: {error.message}</div>}
      </div>
      {articles.map(item => (
        <div className="card" key={item.id}>
          <h2>{item.title}</h2>
          <p>{item.body}</p>
          <p>Published: {item.published ? 'Yes' : 'No'}</p>
          <button onClick={() => updateArticle(item.id, { title: item.title, body: item.body, published: !item.published })}>Update</button>
          <button onClick={() => deleteArticle(item.id)}>Delete</button>
        </div>
      ))}
    </div>
  );
}

export default Article;
