import React, { useState, useEffect } from "react";
import axios from "axios";

function Article() {
    const [articles, setArticles] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        fetchArticles();
    }, []);

    const fetchArticles = async () => {
        try {
            const response = await axios.get("http://localhost:4000/articles");
            setArticles(response.data);
            setLoading(false);
        } catch (error) {
            setError(error.message);
            setLoading(false);
        }
    };

    const handleDelete = async (id) => {
        try {
            await axios.delete(`http://localhost:4000/articles/${id}`);
            setArticles(articles.filter((article) => article.id !== id));
        } catch (error) {
            setError(error.message);
        }
    };

    const handleCreate = async (newArticle) => {
        try {
            const response = await axios.post("http://localhost:4000/articles", newArticle);
            setArticles([...articles, response.data]);
        } catch (error) {
            setError(error.message);
        }
    };

    const handleUpdate = async (id, updatedArticle) => {
        try {
            const response = await axios.put(`http://localhost:4000/articles/${id}`, updatedArticle);
            const updatedArticles = articles.map((article) =>
                article.id === id ? response.data : article
            );
            setArticles(updatedArticles);
        } catch (error) {
            setError(error.message);
        }
    };

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>Error: {error}</div>;
    }

    return (
        <div className="card-container">
            {articles.map((article) => (
                <div className="card" key={article.id}>
                    <h2>{article.title}</h2>
                    <p>{article.body}</p>
                    <button onClick={() => handleDelete(article.id)}>Delete</button>
                    {/* Example: Implement Update/Edit functionality */}
                    {/* <button onClick={() => handleUpdate(article.id, updatedArticleData)}>Edit</button> */}
                </div>
            ))}
            {/* Example: Implement Create/New functionality */}
            {/* <NewArticleForm onCreate={handleCreate} /> */}
        </div>
    );
}

export default Article;
